from sklearn.cluster import SpectralClusteringimport timedef spectral_cluster(X, n_clusters=None):    start = time.time()    if not n_clusters:        n_clusters = len(set(SpectralClustering(n_clusters=3).fit_predict(X)))    model = SpectralClustering(n_clusters=n_clusters, affinity='nearest_neighbors', random_state=42)    y_pred = model.fit_predict(X)    end = time.time()    return y_pred, start, end